import React from "react";
import { useState ,useEffect} from "react";
import '../css/NavBar.css'


const NavBar = () =>{
    const [prevScrollPos,setPrevScrollPos] = useState(0);
    const [visible,setVisible] = useState(true);

    const handleScroll=()=>{
        const currentPos = window.pageYOffset;
        setVisible((prevScrollPos>currentPos)||currentPos<10);
        console.log('current: '+currentPos+'prev:'+prevScrollPos);
        setPrevScrollPos(currentPos);
    }

    useEffect(()=>{
        window.addEventListener('scroll',handleScroll);

        return ()=>window.removeEventListener('scroll',handleScroll);
    },[prevScrollPos,visible,handleScroll]);    


    return (
        <div className ='nav-bar'  style={{top:visible ?'0':'-60px'}}>
            <div className='logo-button'>
                <button>
                <a href='http://localhost:3000/' target='_top'>
                <img className='logo' src='assert/navBar/logo.png' alt='logo'></img>
                </a>
                </button>
            </div>
            <div className='nav-button'>
                <a>
                    .01 Somethings
                </a>
                <a>
                    .02 Somethings
                </a>
                <a>
                    .03 Somethings
                </a>
                <a>
                    .04 Somethings
                </a>
                <button className='resume-button'>
                    <a>
                        Resume
                    </a>
                </button>
            </div>
            <div className='expanse-button'>
                <button>
                    <img className='logo' src='assert/navBar/expanse.png' alt='expanse'></img>
                </button>
            </div>
        </div>
    );
}


export default NavBar;
