import React from "react";
import { useState ,useEffect} from "react";
import '../css/NavBar.css'

const NavBar = () =>{
    const [prevScrollPos,setPrevScrollPos] = useState(0);
    const [navBarVisible,setNavBarVisible] = useState(true);
    const [maxWidth,setMaxWidth] = useState(1000);
    const temp_width = window.innerWidth;
    const [navButtonVisile,setNavButtonVisile]= useState(temp_width>maxWidth/2);
    const [expanseButtonVisible,setExpanseButtonVisible]=useState(temp_width<maxWidth/2)

    const handleScroll=()=>{
        const currentPos = window.pageYOffset;
        setNavBarVisible((prevScrollPos>currentPos)||currentPos<10);
        setPrevScrollPos(currentPos);
    }
    useEffect(()=>{
        window.addEventListener('scroll',handleScroll);

        return ()=>window.removeEventListener('scroll',handleScroll);
    },[prevScrollPos,navBarVisible,handleScroll]);    

    const handleResize=()=>{
        let window_width = window.innerWidth;
        if(window_width>maxWidth)
            setMaxWidth(window_width);
        if(window_width<(maxWidth/2)){
            setNavButtonVisile(false);
            setExpanseButtonVisible(true);
        }else{

            setNavButtonVisile(true);
            setExpanseButtonVisible(false );
        }
    }


    useEffect(()=>{
        window.addEventListener('resize',handleResize);

        return ()=>window.removeEventListener('resize',handleResize);
    },[maxWidth,navButtonVisile,handleResize]); 



    return (
        <div className ='nav-bar'  style={{top:navBarVisible ?'0':'-100px'}}>
            <div className='logo-button'>
                <button>
                <a href='http://localhost:3000/' target='_top'>
                <img className='logo' src='assert/navBar/logo.svg' alt='logo'></img>
                </a>
                </button>
            </div>
            <div className='nav-button' style={{display:(navButtonVisile?'flex':'none')}}>
                <a href="#home">
                    .01 Somethings
                </a>
                <a href="#home">
                    .02 Somethings
                </a>
                <a href="#home">
                    .03 Somethings
                </a>
                <a href="#home">
                    .04 Somethings
                </a>
                <button className='resume-button'>
                    <a href="#home">
                        Resume
                    </a>
                </button>
            </div>
            <div className='expanse-button' style={{display:(expanseButtonVisible?'flex':'none')}}>
                <button>
                <svg className='logo'  viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M21.87,11.5l-4.5-7.79a1,1,0,0,0-.87-.5h-9a1,1,0,0,0-.87.5L2.13,11.5a1,1,0,0,0,0,1l4.5,7.79a1,1,0,0,0,.87.5h9a1,1,0,0,0,.87-.5l4.5-7.79A1,1,0,0,0,21.87,11.5Zm-6,7.29H8.08L4.15,12,8.08,5.21h7.84L19.85,12Z"/></svg>
                    {/* <img className='logo' src='assert/navBar/expanse.svg' alt='expanse'></img> */}
                    {/* width="24px" height="24px" */}
                </button>
            </div>
        </div>
    );
}


export default NavBar;
