import React from "react";
import { useState ,useEffect} from "react";
import '../css/NavBar.css'

const NavBar = () =>{
    const [prevScrollPos,setPrevScrollPos] = useState(0);
    const [navBarVisible,setNavBarVisible] = useState(true);
    const [maxWidth,setMaxWidth] = useState(1000);
    const [navButtonVisile,setNavButtonVisile]= useState(true);
    const handleScroll=()=>{
        const currentPos = window.pageYOffset;
        setNavBarVisible((prevScrollPos>currentPos)||currentPos<10);
        setPrevScrollPos(currentPos);
    }

    useEffect(()=>{
        window.addEventListener('scroll',handleScroll);

        return ()=>window.removeEventListener('scroll',handleScroll);
    },[prevScrollPos,navBarVisible,handleScroll]);    

    const handleResize=()=>{
        let window_width = window.innerWidth;
        console.log(window_width);
        if(window_width>maxWidth)
            setMaxWidth(window_width);
        if(window_width<(maxWidth/2)){
            setNavButtonVisile(false);
        }else{

            setNavButtonVisile(true);
        }
    }


    useEffect(()=>{
        window.addEventListener('resize',handleResize);

        return ()=>window.removeEventListener('resize',handleResize);
    },[maxWidth,navButtonVisile,handleResize]); 

    useEffect(()=>{
        window.addEventListener('load',handleResize);
        console.log('Test');
        return ()=>window.removeEventListener('load',handleResize);
    },[maxWidth,navButtonVisile,handleResize]); 


    return (
        <div className ='nav-bar'  style={{top:navBarVisible ?'0':'-60px'}}>
            <div className='logo-button'>
                <button>
                <a href='http://localhost:3000/' target='_top'>
                <img className='logo' src='assert/navBar/logo.png' alt='logo'></img>
                </a>
                </button>
            </div>
            <div className='nav-button' style={{visibility:(navButtonVisile?'visible':'hidden')}}>
                <a href="#home">
                    .01 Somethings
                </a>
                <a href="#home">
                    .02 Somethings
                </a>
                <a href="#home">
                    .03 Somethings
                </a>
                <a href="#home">
                    .04 Somethings
                </a>
                <button className='resume-button'>
                    <a href="#home">
                        Resume
                    </a>
                </button>
            </div>
            <div className='expanse-button'>
                <button>
                    <img className='logo' src='assert/navBar/expanse.png' alt='expanse'></img>
                </button>
            </div>
        </div>
    );
}


export default NavBar;
